INSERT INTO `news` (`id`, `title`, `slug`, `content`, `excerpt`, `status`, `category`, `featured_image`, `author_id`, `view_count`, `published_at`, `created_at`, `updated_at`) VALUES (179,'เธเธขเธฒเธขเน€เธงเธฅเธฒเธฃเธฑเธเธชเธกเธฑเธเธฃ เนเธเธฃเธเธเธฒเธฃเธเธฒเธฃเธญเธเธฃเธกเน€เธเธดเธเธเธเธดเธเธฑเธ•เธดเธเธฒเธฃเธซเธฅเธฑเธเธชเธนเธ•เธฃเธเธฑเธ’เธเธฒเธจเธฑเธเธขเธ เธฒเธเธเธฃเธนเน€เธเธทเนเธญเน€เธเนเธเธเธนเนเธญเธณเธเธงเธขเธเธฒเธฃเน€เธฃเธตเธขเธเธฃเธนเน เนเธ”เธข เธชเธชเธงเธ—. เนเธฅเธฐเธกเธซเธฒเธงเธดเธ—เธขเธฒเธฅเธฑเธขเธฃเธฒเธเธ เธฑเธเธญเธธเธ•เธฃเธ”เธดเธ•เธ–เน','เธเธขเธฒเธขเน€เธงเธฅเธฒเธฃเธเธชเธกเธเธฃ-เนเธเธฃเธเธเธฒเธฃเธเธฒเธฃเธญเธเธฃเธกเน€เธเธเธเธเธเธ•เธเธฒเธฃเธซเธฅเธเธชเธ•เธฃเธเธ’เธเธฒเธจเธเธขเธ เธฒเธเธเธฃเน€เธเธญเน€เธเธเธเธญเธณเธเธงเธขเธเธฒเธฃเน€เธฃเธขเธเธฃ-เนเธ”เธข-เธชเธชเธงเธ—-เนเธฅเธฐเธกเธซเธฒเธงเธ—เธขเธฒเธฅเธขเธฃเธฒเธเธ เธเธญเธ•เธฃเธ”เธ•เธ–','เน€เธเธตเนเธขเธงเธเธฑเธเธเธ“เธฐ\nเนเธเธฐเธเธณเธเธ“เธฐ\nเธเธฃเธฑเธเธเธฒ/เธงเธดเธชเธฑเธขเธ—เธฑเธจเธเน/เธญเธฑเธ•เธฅเธฑเธเธฉเธ“เน/เธเธฑเธเธเธเธดเธ\nเธซเธฅเธฑเธเธชเธนเธ•เธฃ\nเธเธธเธเธฅเธฒเธเธฃ\nเน€เธงเนเธเนเธเธ•เนเธซเธเนเธงเธขเธเธฒเธเธเธญเธเธเธ“เธฐ\nเธเธฒเธเธงเธดเธเธฑเธขเนเธฅเธฐเธงเธดเธเธฒเธเธฒเธฃ\nเธจเธนเธเธขเนเธ”เธดเธเธดเธ—เธฑเธฅเน€เธเธทเนเธญเธเธฑเธ’เธเธฒเธ—เนเธญเธเธ–เธดเนเธ\nเธซเธเนเธงเธขเธเธฑเธ”เธเธฒเธฃเธเธฒเธเธงเธดเธเธฑเธขเนเธฅเธฐเธเธฑเธเธเธเธดเธเธชเธฑเธกเธเธฑเธเธเน\nเธงเธฒเธฃเธชเธฒเธฃเธงเธดเธเธฒเธเธฒเธฃเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเนเธฅเธฐเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเธเธฃเธฐเธขเธธเธเธ•เน\nเธงเธฒเธฃเธชเธฒเธฃเธเธ“เธดเธ•เธจเธฒเธชเธ•เธฃเนเธฃเธฒเธเธ เธฑเธ\nเธจเธนเธเธขเนเธเธฃเธฐเธชเธฒเธเธเธฒเธเนเธเธฃเธเธเธฒเธฃ CSRM\nเธจเธนเธเธขเนเธเธฅเธฑเธเธเธฒเธเนเธฅเธฐเธชเธดเนเธเนเธงเธ”เธฅเนเธญเธก\nเธชเธฑเธเธ”เธฒเธซเนเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเนเธซเนเธเธเธฒเธ•เธด\nAUCC2025\nเธเธฃเธดเธเธฒเธฃ\nเน€เธญเธเธชเธฒเธฃเนเธเธเธเธญเธฃเนเธกเธ”เธฒเธงเธเนเนเธซเธฅเธ”\nเน