INSERT INTO `news` (`id`, `title`, `slug`, `content`, `excerpt`, `status`, `category`, `featured_image`, `author_id`, `view_count`, `published_at`, `created_at`, `updated_at`) VALUES (303,'เนเธเธฃเธเธเธฒเธฃ โ€เธซเธเธถเนเธเธซเธกเธนเนเธเนเธฒเธ เธซเธเธถเนเธเน€เธเธเธญเธฒเธซเธฒเธฃเนเธ—เธขโ€ 1 เนเธเนเธเธเธขเธธเธ—เธเธจเธฒเธชเธ•เธฃเนเธเธญเธเธ•เนเธเธฒเธงเน€เธงเธญเธฃเนเธเธฑเธเน€เธเธฅเธทเนเธญเธเธญเธธเธ•เธชเธฒเธซเธเธฃเธฃเธกเธ”เนเธฒเธเธญเธฒเธซเธฒเธฃ เน€เธเธทเนเธญเธชเธฃเนเธฒเธเนเธญเธเธฒเธช เธชเธฃเนเธฒเธเธเธฒเธ เธชเธฃเนเธฒเธเธฃเธฒเธขเนเธ”เนเนเธซเธกเนเนเธซเนเธเธเนเธ—เธข','เนเธเธฃเธเธเธฒเธฃ-เธซเธเธเธซเธกเธเธฒเธ-เธซเธเธเน€เธเธเธญเธฒเธซเธฒเธฃเนเธ—เธข-1-เนเธเนเธเธเธขเธ—เธเธจเธฒเธชเธ•เธฃเธเธญเธเธ•เธเธฒเธงเน€เธงเธญเธฃเธเธเน€เธเธฅเธญเธเธญเธ•เธชเธฒเธซเธเธฃเธฃเธกเธ”เธฒเธเธญเธฒเธซเธฒเธฃ-เน€เธเธญเธชเธฃเธฒเธเนเธญเธเธฒเธช-เธชเธฃเธฒเธเธเธฒเธ-เธชเธฃเธฒเธเธฃเธฒเธขเนเธ”เนเธซเธกเนเธซเธเธเนเธ—เธข-1','เน€เธเธตเนเธขเธงเธเธฑเธเธเธ“เธฐ\nเนเธเธฐเธเธณเธเธ“เธฐ\nเธเธฃเธฑเธเธเธฒ/เธงเธดเธชเธฑเธขเธ—เธฑเธจเธเน/เธญเธฑเธ•เธฅเธฑเธเธฉเธ“เน/เธเธฑเธเธเธเธดเธ\nเธซเธฅเธฑเธเธชเธนเธ•เธฃ\nเธเธธเธเธฅเธฒเธเธฃ\nเน€เธงเนเธเนเธเธ•เนเธซเธเนเธงเธขเธเธฒเธเธเธญเธเธเธ“เธฐ\nเธเธฒเธเธงเธดเธเธฑเธขเนเธฅเธฐเธงเธดเธเธฒเธเธฒเธฃ\nเธจเธนเธเธขเนเธ”เธดเธเธดเธ—เธฑเธฅเน€เธเธทเนเธญเธเธฑเธ’เธเธฒเธ—เนเธญเธเธ–เธดเนเธ\nเธซเธเนเธงเธขเธเธฑเธ”เธเธฒเธฃเธเธฒเธเธงเธดเธเธฑเธขเนเธฅเธฐเธเธฑเธเธเธเธดเธเธชเธฑเธกเธเธฑเธเธเน\nเธงเธฒเธฃเธชเธฒเธฃเธงเธดเธเธฒเธเธฒเธฃเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเนเธฅเธฐเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเธเธฃเธฐเธขเธธเธเธ•เน\nเธงเธฒเธฃเธชเธฒเธฃเธเธ“เธดเธ•เธจเธฒเธชเธ•เธฃเนเธฃเธฒเธเธ เธฑเธ\nเธจเธนเธเธขเนเธเธฃเธฐเธชเธฒเธเธเธฒเธเนเธเธฃเธเธเธฒเธฃ CSRM\nเธจเธนเธเธขเนเธเธฅเธฑเธเธเธฒเธเนเธฅเธฐเธชเธดเนเธเนเธงเธ”เธฅเนเธญเธก\nเธชเธฑเธเธ”เธฒเธซเนเธงเธดเธ—เธขเธฒเธจเธฒเธชเธ•เธฃเนเนเธซเนเธเธเธฒเธ•เธด\nAUCC2025\nเธเธฃเธดเธเธฒเธฃ\nเน€เธญเธ